            a.substr(0, 4)
          )
        )
          check = true
      })(navigator.userAgent || navigator.vendor || window.opera)
      return check
    }

    this.send("party", {
      xpath: "/html/body",
      percent: 0,
      isMobile: mobileCheck(),
      status: window.CROWWWD.ONLINE,
      pic: `/img/${Math.round(Math.random() * 9)}.jpg`, // TODO: Improve so that it is not sent everytime
    })

    document.addEventListener("mouseover", (e) => {
      try {
        const el = e.target || e.srcElement

        this.self.party.xpath = xpath(el)
        this.self.party.status = window.CROWWWD.ONLINE

        clearTimeout(this.awayTimeout)
        this.awayTimeout = setTimeout(() => {
          this.self.party.status = window.CROWWWD.AWAY
          this.sync("party")
        }, 5000)

        this.sync("party")
      } catch (e) { /* Ignore when we are not in the DOM */ }
    })

    document.addEventListener("scroll", (event) => {
      const max = document.documentElement.scrollHeight - window.innerHeight
      const percent = Math.round((window.scrollY * 100) / max)
      this.self.party.percent = percent
      this.sync("party")
    })
  },
  middleware: {
    $: (data, username, isSelf) => {
      return data
    },
    party: (data, username, isSelf) => {
      let rect

      if (!data.isMobile) {
        try {
          rect = xpath(data.xpath).getBoundingClientRect()
        } catch (e) {
          rect = { x: 0, y: 0 }
        }
        data.pos = {
          x: Math.round(rect.x + document.documentElement.scrollLeft),
          y: Math.round(rect.y + document.documentElement.scrollTop),
        }
      } else {
        data.pos = {
          x: 50,
          y:
            (data.percent * (document.documentElement.scrollHeight - window.innerHeight)) / 100 +
            window.innerHeight / 2 +
            1,
        }
      }

      if (data.pos.y > scrollY + innerHeight) {
        data.wayOut = "DOWN"
        data.pos.y = scrollY + innerHeight - 70
      } else if (data.pos.y < scrollY) {
        data.wayOut = "UP"
        data.pos.y = scrollY
      } else {
        data.wayOut = undefined
      }

      return data
    },
  },
})

